var app=new Vue({el:"#app",data:{scanner:null,activeCameraId:null,cameras:[],scans:[]},mounted:function(){var n=this;n.scanner=new Instascan.Scanner({video:document.getElementById("preview"),scanPeriod:5}),n.scanner.addListener("scan",function(a){n.scans.unshift({date:+Date.now(),content:a})}),Instascan.Camera.getCameras().then(function(a){n.cameras=a,a.length>0?(n.activeCameraId=a[0].id,n.scanner.start(a[0])):console.error("No cameras found.")})["catch"](function(n){console.error(n)})},methods:{formatName:function(n){return n||"(unknown)"},selectCamera:function(n){this.activeCameraId=n.id,this.scanner.start(n)}}});